const rawString = $('產生分鏡').first().json.data;// 獲取你的輸入字串
const startDelimiter = '```json\n';
const endDelimiter = '\n```'; // 注意：這裡的結束分隔符只包含換行和三個反引號
const startIndex = rawString.indexOf(startDelimiter);
const endIndex = rawString.indexOf(endDelimiter, startIndex); // 從開始分隔符之後尋找結束分隔符
let jsonString;

if (startIndex !== -1 && endIndex !== -1) {
    // 找到了開頭和結尾分隔符
    // 從 '```json\n' 的下一個字元開始，直到 '\n```' 的前一個字元
    jsonString = rawString.substring(startIndex + startDelimiter.length, endIndex);
} else {
    // 如果沒有找到特定的包裹格式，則假設整個字串就是 JSON
    console.warn('String does not seem to be wrapped with "```json\\n" and "\\n```". Attempting direct parse.');
    jsonString = rawString;
}
try {
    const jsonObject = JSON.parse(jsonString);
    return jsonObject;
} catch (e) {
    throw new Error(`Failed to parse JSON. Raw string (or extracted part): "${jsonString.substring(0, 200)}..." Error: ${e.message}`);
}

