const base64ImagesArray = []; // 用於存放所有 Base64 字串的陣列
// 遍歷所有傳入 Code 節點的 Item
for (const item of $input.all()) {
    // 檢查當前 Item 是否有二進位資料
    if (item.binary && Object.keys(item.binary).length > 0) {
        // 一個 Item 的 binary 物件可能有多個二進位檔案
        for (const binaryKey in item.binary) {
            // 確保是物件自身的屬性
            if (Object.hasOwnProperty.call(item.binary, binaryKey)) {
                const binaryFile = item.binary[binaryKey];
                const dataBuffer = binaryFile.data; // 這是 n8n 內部表示的 Base64 編碼緩衝區字串

                if (dataBuffer) {
                    // 將 n8n 的二進位資料 (Base64 編碼的緩衝區字串) 轉換為真正的 Buffer
                    // 然後再將 Buffer 轉換為 Base64 字串
                    const base64String = Buffer.from(dataBuffer, 'base64').toString('base64');

